    /**
     * @param string $name Is name of tag Select
     * @return string Representation of tag Select
     */
    public static function SelectRegion(string $name = ''): string
    {
        $out = '<select name="' . $name . '">' . xbr;
        foreach (self::$regs as $idz => $itemz) {
            $out .= '<optgroup label="' . $itemz['title'] . '">' . xbr;
            foreach (self::$distrs as $idx => $itemx) {
                if ($itemz['idx'] == $itemx['region']) {
                    $out .= '<option value="' . $itemz['idx'] . '&' . $itemx['idx'] . '">' . $itemx['title'] . '</option>' . xbr;
                }
            }
            $out .= '</optgroup>';
        }
        return $out . '</select>' . xbr;
    }
